mapper.select:
SELECT sum(if(cm.total_amount-cm.down_amount-cm.final_amount < ppl.amount,cm.total_amount-cm.down_amount-cm.final_amount, ppl.amount)+if(cm.period_end_date < #{endDate},cm.final_amount,0)) from maple_co_model as cm RIGHT JOIN maple_period_plan as ppl on ppl.co_model_id=cm.id WHERE cm.car_id in (SELECT DISTINCT (d.car_id) from maple_driver as d WHERE d.driver_status=1) and (ppl.start_date <= #{endDate} and ppl.end_date >= #{startDate})
and cm.model_type = #{coModelType}
and cm.car_id in (select id from maple_car where branch = #{branch})
